import { createWebHistory, createRouter } from "vue-router";
import HomePage from "./components/HomePage.vue";
import RegisterPage from "./components/account/page/RegisterPage.vue";
import TodoPage from "./components/todo/page/TodoPage.vue";
import MailPage from "./components/mail/page/MailPage.vue";
import ConferPage from "./components/conference/page/ConferPage.vue";
import NoticePage from "./components/notice/page/NoticePage.vue";
import NoticeTextPage from "./components/notice/page/NoticeTextPage.vue";
import NoticeWritePage from "./components/notice/page/NoticeWritePage.vue";
import InformationPage from "./components/account/page/InformationPage.vue";

// lazy-loaded
const ProfilePage = () => import("./components/refs/ProfilePage.vue")

// component
import ModalPage from "./components/common/component/ModalPage";
import LoginComponent from "./components/account/component/LoginComponent.vue";
// lazy-loaded
const BoardAdmin = () => import("./components/refs/BoardAdmin.vue")
const BoardModerator = () => import("./components/refs/BoardModerator.vue")
const BoardUser = () => import("./components/refs/BoardUser.vue")


const routes = [
  {
    path: "/",
    name: "HomePage",
    component: HomePage,
  },
  {
    path: "/registerpage",
    component: RegisterPage,
  },
  // {
  //   path: "/searchpage",
  //   component: SearchPage,
  // },
  {
    path: "/login",
    component: LoginComponent,
  },
  {
    path: "/info",
    component: InformationPage,
  },
  {
    path: "/modalpage",
    component: ModalPage,
  },
  {
    path: "/todopage",
    component: TodoPage,
  },
  {
    path: "/mailpage",
    component: MailPage,
  },
  {
    path: "/conferpage",
    component: ConferPage,
  },
  {
    path: "/notice",
    component: NoticePage,
    children : [
      {
        path : "/:num",
        component : NoticeTextPage
      },
      {
        path: "write",
        component: NoticeWritePage,
      }
    ]
  },
  // {
  //   path: "/noticepage",
  //   component: NoticePage,
  // },
  // {
  //   path: "/noticepage/:num",
  //   component: NoticeTextPage,
  // },
  // {
  //   path: "/notice/write",
  //   component: NoticeWritePage,
  // },
  {
    path: "/profilepage",
    name: "profilepage",
    // lazy-loaded
    component: ProfilePage,
  },
  {
    path: "/admin",
    name: "adminpage",
    // lazy-loaded
    component: BoardAdmin,
  },
  {
    path: "/mod",
    name: "moderator",
    // lazy-loaded
    component: BoardModerator,
  },
  {
    path: "/user",
    name: "user",
    // lazy-loaded
    component: BoardUser,
  },
];
const router = createRouter({
  history: createWebHistory(),
  routes,
});


