    component: NoticeDetailPage,
    props: route => ({
      id: Number(route.params.id)
    })
  },
  {
    path: "/notice/write",
    component: NoticeWritePage,
  },






  {
    path: "/profilepage",
    name: "profilepage",
    // lazy-loaded
    component: ProfilePage,
  },
  {
    path: "/admin",
    name: "adminpage",
    // lazy-loaded
    component: BoardAdmin,
  },
  {
    path: "/mod",
    name: "moderator",
    // lazy-loaded
    component: BoardModerator,
  },
  {
    path: "/user",
    name: "user",
    // lazy-loaded
    component: BoardUser,
  },
];
const router = createRouter({
  history: createWebHistory(),
  routes,
});


