import { createWebHistory, createRouter } from "vue-router";
import HomePage from "./components/HomePage.vue";
import RegisterPage from "./components/RegisterPage.vue";
import SearchPage from "./components/SearchPage.vue";
import ModalPage from "./components/ModalPage.vue";
import TodoPage from "./components/TodoPage.vue";
import MailPage from "./components/MailPage.vue";
import ConferPage from "./components/ConferPage.vue";
import NoticePage from "./components/NoticePage.vue";
// lazy-loaded
const ProfilePage = () => import("./components/ProfilePage.vue")
const BoardAdmin = () => import("./components/BoardAdmin.vue")
const BoardModerator = () => import("./components/BoardModerator.vue")
const BoardUser = () => import("./components/BoardUser.vue")
const routes = [
  {
    path: "/",
    name: "HomePage",
    component: HomePage,
  },
  {
    path: "/registerpage",
    component: RegisterPage,
  },
  {
    path: "/searchpage",
    component: SearchPage,
  },
  {
    path: "/modalpage",
    component: ModalPage,
  },
  {
    path: "/todopage",
    component: TodoPage,
  },
  {
    path: "/mailpage",
    component: MailPage,
  },
  {
    path: "/conferpage",
    component: ConferPage,
  },
  {
    path: "/noticepage",
    component: NoticePage,
  },
  {
    path: "/noticepage/:num",
    name: "NoticeTextPage",
    component: NoticeText,
  },
  {
    path: "/profilepage",
    name: "profilepage",
    // lazy-loaded
    component: ProfilePage,
  },
  {
    path: "/admin",
    name: "adminpage",
    // lazy-loaded
    component: BoardAdmin,
  },
  {
    path: "/mod",
    name: "moderator",
    // lazy-loaded
    component: BoardModerator,
  },
  {
    path: "/user",
    name: "user",
    // lazy-loaded
    component: BoardUser,
  },
];
const router = createRouter({
  history: createWebHistory(),
  routes,
});


